VALUES (#{carereservationName}, #{carereservationType},#{carereservationAge},#{carereservationPhoneNum},#{carereservationGender},#{carereservationAddPhoneNum},#{carereservationRelation},#{carereservationEndAddress},#{carereservationStartAddress},#{carereservationHopeGender},#{carereservationDate},#{carereservationTime},#{carereservationAddRequire},#{carereservationPrice})
WHERE USER_EMAIL = #{userEmail}

mapper.select:
