mapper.select:
SELECT REVIEW_NUM, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_WRITER, REVIEW_SCORE,REVIEW_DATE, REVIEW_UPDATE_DATE
FROM TBL_REVIEW
WHERE REVIEW_NUM = #{reviewNum}

mapper.update:
UPDATE TBL_REVIEW
SET REVIEW_TITLE = #{reviewTitle}, REVIEW_CONTENT = #{reviewContent},REVIEW_SCORE= #{reviewScore}, REVIEW_UPDATE_DATE = SYSDATE
WHERE REVIEW_NUM = #{reviewNum}
